package string;

public class LongestAEIOUSequence {

    public static void main(String[] args) {
        System.out.println(longestBeautifulSubstring("\""));
    }
    public static int longestBeautifulSubstring(String word) {
        int l=0, r=0, f=0, len=0;
        while(r<word.length()){
            if( f==0 && word.charAt(r)=='a' ){
                f = 1;
                while(word.charAt(r)== word.charAt(r+1))
                    r++;
                r++;
                if(f==1 && word.charAt(r)=='e' ){
                    f=2;
                    while(word.charAt(r)== word.charAt(r+1))
                        r++;
                    r++;
                    if(f==2 && word.charAt(r)=='i' ){
                        f=3;
                        while(word.charAt(r)== word.charAt(r+1))
                            r++;
                        r++;
                        if(f==3 && word.charAt(r)=='o' ){
                            f=4;
                            while(word.charAt(r)== word.charAt(r+1))
                                r++;
                            r++;
                            if(f==4 && word.charAt(r)=='u' ){
                                while(r+1 < word.length() && word.charAt(r)== word.charAt(r+1))    {
                                    r++;
                                }

                                len = r-l+1;

                                if(r+1 == word.length()) break;
                            }
                            else{
                                l = r;
                                f = 0;
                            }
                        }
                        else{
                            l = r;
                            f = 0;
                        }
                    }
                    else{
                        l = r;
                        f = 0;
                    }
                }
                else{
                    l = r;
                    f = 0;
                }
            }
            else{
                l = r+1 < word.length()? ++r:r;
                f = 0;
            }


        }
        return len;
    }

}
